ns9:component-version-script.script:
#!/bin/sh # vFabric ApplicationDirector START script for vFabric tc Server
# From ApplicationDirector - Import and source global configuration . $global_conf
# This script simply starts the tc Server instance created in the tcserver-config.sh sample script.
set -e
export PATH=$PATH:/usr/sbin:/sbin:/usr/bin:/bin export HOME=/root export tcserver_home=${tcserver_home:="/opt/vmware/vfabric-tc-server-standard"}
export JAVA_HOME=${java_home:="/usr"}
if [ -f ${tcserver_home}/${instance_name}/bin/tcruntime-ctl.sh ]; then $service_start IS_RUNNING=`$instance_dir/bin/tcruntime-ctl.sh status | grep Status | awk -F: '{ print $2 }'` if [[ "${IS_RUNNING}" == *"NOT RUNNING"* ]]; then echo "ERROR: ${tcserver_home}/${instance_name} is NOT RUNNING." echo "Please check the logs in ${tcserver_home}/${instance_name}/logs for more information" exit 1 else echo "COMPLETED: The status of your new tc Server instance is: ${IS_RUNNING}" fi fi

#!/bin/sh

## FUNCTION TO DISPLAY ERROR AND EXIT
function check_error()
{
    if [ ! "$?" = "0" ]; then
        error_exit "$1";
    fi
}

function error_exit()
{
    echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2
    exit 1
}

env > /tmp/env.txt

# Stop the application server. This parameter should point to the script used to stop the application server.
echo "stop the service"
$service_stop

# Ensure that webapps_dir property is assigned to the application server webapps_dir property.
echo "copy the war file ${war_file} to ${webapps_dir}/${context}.war"
cp -f ${war_file} ${webapps_dir}/${context}.war
check_error "Cannot copy the war file to the webapp directory"

# Start the application server. This parameter should point to the script used to start the application server.
echo "start the service"
$service_start

#!/bin/sh
# This is the UPDATE lifecycle script for WAR component to update $war_file. # If other properties are changed, the script will need to be adjusted accordingly.
env > /tmp/env.txt
## NAME OF SUPPORTED PROPS. ## ADD MORE PROP NAMES IF THERE ARE MORE PROPS SUPPORTED FOR UDPATE. NAME_WAR_FILE="war_file"
## ARRAY OF SUPPORTED PROPS SUPPORTED_PROPS=($NAME_WAR_FILE)
## FUNCTION TO CHECK IF THE STRING IN THE ARRAY containsElement () { local e for e in "${@:2}"; do [[ "$e" == "$1" ]] && return 0; done return 1 }
## FUNCTION TO DISPLAY ERROR AND EXIT function check_error() { if [ ! "$?" = "0" ]; then error_exit "$1"; fi }
function error_exit() { echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2 exit 1 }
echo "WAR component UPDATE to update war_file."
## STOP THE SCRIPT IF UNSUPPORTED PROPERTIES UPDATED declare -a unsupported_props for v in ${APPD_UPDATE_PROPS[*]}; do if ! containsElement "$v" "${SUPPORTED_PROPS[@]}"; then unsupported_props+=($v) fi done if [ ${#unsupported_props[*]} -gt 0 ]; then echo This update script does not support the update of the following properties: ${unsupported_props[*]} exit 1; fi
## UPDATE WAR FILE if containsElement "$NAME_WAR_FILE" "${APPD_UPDATE_PROPS[@]}"; then # Stop the application server. This parameter should point to the script used to stop the application server. echo "stop the service" $service_stop
# remove the old EXPANDED context to trigger the application server to expand the updated context rm -rf ${webapps_dir}/${context}
# Ensure that webapps_dir property is assigned to the application server webapps_dir property. echo "copy ${war_file} to ${webapps_dir}/${context}.war" cp -f ${war_file} ${webapps_dir}/${context}.war check_error "Cannot copy the war file to the webapp directory"
# Start the application server. This parameter should point to the script used to start the application server. echo "start the service" $service_start fi
echo "Finished WAR component UPDATE"

